         var offset_l = $(this).offset().left - $(window).scrollLeft();

         var left = Math.round( (e.clientX - offset_l) );
         var top = Math.round( (e.clientY - offset_t) );

         $('#tail').css({
            left:  left+40,
            top:   top+60
            });

         top = top - 48;
         left = left - 7;
     
         latClicked = urlat[view] - (top * pixPerLat);
         lonClicked = lllon[view] + (left * pixPerLon);
         
         findNearestReports(latClicked,lonClicked, $(this).attr("id").split("_")[0])      
         });

      }
      
  });
  
  
