//==========================================================//
//              function updateTabs(availMaps)              //
//                                                          //
// Get the list of available maps from xml file and get     //
// the lat/lon bounds as well. Call other functions to      //
// to update the display.                                   //
//==========================================================//
function updateTabs(availMaps) {

   //--------------------//
   // Reset tab variable //
   //--------------------//   
   tabAvailibtyConfig={"Snow":{'box':false,'neweng':false},"Snow24":{'box':false,'neweng':false},
      "Pcpn":{'box':false,'neweng':false},"Pcpn24":{'box':false,'neweng':false},
      "Ice":{'box':false,'neweng':false},"Ice24":{'box':false,'neweng':false},
      "Wind":{'box':false,'neweng':false},"Gust":{'box':false,'neweng':false}};     
   
   //--------------------------------------------//   
   // Loop through the available map list to see //
   // which tabs need to be visible.             //
   //--------------------------------------------//
   for (var key in tabAvailibtyConfig) {
      node = $(availMaps).find(key)
      if (node.length > 0) {      
         for (var vw in views ) {
            if ($(node).find(views[vw]).text() === "1") {
                tabAvailibtyConfig[key][views[vw]] = true;
               }
            }
         }
      }
      
   //--------------------------------------------------------//      
   // get the bounding lat/lon's for each view and calculate //
   // the pixels/ per lat and lon.                           //
   //--------------------------------------------------------//
   for (var i=0; i < views.length; i++) {
      urlat[views[i]] = parseFloat($(availMaps).find("viewBounds").find(views[i]).find('urlat').text());  
      urlon[views[i]] = parseFloat($(availMaps).find("viewBounds").find(views[i]).find('urlon').text());
      lllat[views[i]] = parseFloat($(availMaps).find("viewBounds").find(views[i]).find('lllat').text());  
      lllon[views[i]] = parseFloat($(availMaps).find("viewBounds").find(views[i]).find('lllon').text());
      }
      
   //----------------------------------//         
   // now call the different functions //
   // to update tabs and images.       //
   //----------------------------------//   
   adjustTabs();
   updateImage();
   loadPnsText();
      
   return;
 
}

function adjustTabs() {
   
   for (var key in tabAvailibtyConfig) {   

      //--------------------------------------------------//      
      // hide any tab that there isn't any content for if //
      // it hasn't already been hidden.                   //
      //--------------------------------------------------//
      if (tabAvailibtyConfig[key][view] === false) {
         if (tabAvailibtyConfigCrnt[key][view] === true) {
            $("#"+key).hide();
            $("#"+key + "-li").hide();
            }
         }  
         
      //-------------------------------------------//               
      // if tab was previously hidden but there is //
      // content for this event, then show the tab //
      //-------------------------------------------//      
      if (tabAvailibtyConfig[key][view] === true) {
         console.log(key + ' ' +tabAvailibtyConfig[key][view])
         console.log(key + ' ' +tabAvailibtyConfigCrnt[key][view])
         if (tabAvailibtyConfigCrnt[key][view] === false) {
            console.log('showing')
            $("#"+key).show();
            $("#"+key + "-li").show();
            }
         }           
      }
      
   //------------------------------------//      
   // set focus to the first active tab. //
   //------------------------------------//
   cnt = 0;
   for (var key in tabAvailibtyConfig) {   
      if (tabAvailibtyConfig[key][view] === true) {
         $("#tabs-outer").tabs("option", "active", cnt);
         }
     cnt++;               
   } 
   cnt = 0;
   for (var key in tabAvailibtyConfig) {   
      if (tabAvailibtyConfig[key][view] === true) {
         $("#tabs-outer").tabs("option", "active", cnt);
         break;
         }
     cnt++;               
   } 

   //---------------------------------------------//   
   // Store the current tab config for later use. //
   //---------------------------------------------//
   tabAvailibtyConfigCrnt = tabAvailibtyConfig;

   return;
}  


//-----------------------------------------------------//
//                function updateImage()               //
// Function to update the image on the image tab.      //
//-----------------------------------------------------//
function updateImage() {

   for (var key in tabAvailibtyConfig) {   
      if (tabAvailibtyConfig[key][view] !== false) {
         var imgName = eventDate +"/" + eventDate + "_" + key + "_" + view+ ".png";
         $("#"+key+"_img").attr("src", imageUrl + imgName);
         $("#"+key).attr("href", imageUrl + imgName);         
         $("#"+key+"_img").css("height", imageSize[view][1]);         
         $("#"+key+"_img").css("width", imageSize[view][0]);  
         $("#"+key+"_alt").attr("href", imageUrl + imgName);         
         }        
      }

   latDelta = urlat[view] - lllat[view];
   lonDelta = urlon[view] - lllon[view];
   console.log(latDelta+ "  "+lonDelta)         
   pixPerLon = lonDelta/(imageSize[view][0]- 13);
   pixPerLat = latDelta/(imageSize[view][1] - 97);      

   return;
}

