//-----------------------------------------------------//
//           function findNearestReports(lat,lon)      //
//-----------------------------------------------------//
function findNearestReports(lat,lon, elmType) {

   divHeight = {1:50,2:60, 3:75, 4:90,5:105, 6:120}
   if (elmType === "Snow" || elmType === "Pcpn" || elmType === "Ice") {
      var unit = "Amount<br>Inches";
      }
   else {
      var unit = "Speed<br>MPH";
      }
   
   var reportsFound = new Object;

   //---------------------------------------------------------------//
   // loop through the xml finding all reports for the elementType, //
   // calculate the distance of each report from the clicked        //
   // location, and if within 'searchRadius', show these reports    //
   //---------------------------------------------------------------//
   $(reportsXml).find(elmType).each(function () {
      $(this).children('report').each(function () {
         myLoc = $(this).find('location').text();
         myLat = parseFloat($(this).find('latitude').text());
         myLon = parseFloat($(this).find('longitude').text());
         myAmnt = parseFloat($(this).find('value').text());
         myDis = distance(lat, lon, myLat, myLon, 'M').toFixed(3);
         if (myDis <= searchRadius) {
            reportsFound[myDis] = {'location': myLoc, 'amount':myAmnt, 'dist':(Math.round(myDis * 10 ) / 10).toFixed(1)};
            }
         });           
      });
      
   //-----------------------------------//      
   // sort the distances so the closest //
   // reports are first.                //
   //-----------------------------------//
   keysArray = [];  
   for (var mkey in reportsFound){     
      keysArray.push(mkey);
      }   
   keysArray.sort();

   //--------------------------------------------//
   // loop through the sorted distance keys and  //
   // create the html with the reports (location //
   // and depth) and update the tag so the info  //
   // is displayed on the page.                  //
   //--------------------------------------------//
   htmlStr = "<table>";
   htmlStr += "<tr><th>Report<br>Location</th><th>" + unit + "</th></tr>";   
   cnt = 0;
   for (var v in keysArray) {
      htmlStr += "<tr><td>"+reportsFound[keysArray[v]]['location'] + "</td><td style=\"text-align:center\">" + reportsFound[keysArray[v]]['amount']+ "</td></tr>";      
      cnt += 1;
      if (cnt >= 6) {
         break;
         }
      }
      
   htmlStr += "</table>";   
   
   if (cnt > 0) {
      $("#tail").html(htmlStr+"</pre>");
      $("#tail").css('visibility', 'visible');
      $("#tail").height(divHeight[cnt]);
      }
   else {
      $("#tail").css('visibility', 'hidden');;
      }
   return;   
}

      
