// functions below

// function to grab variables from the url
function getQueryVar(varName){
	var queryStr = unescape(window.location.search) + '&';
	var regex = new RegExp('.*?[&\?]' + varName + '=(.*?)&.*');
	val = queryStr.replace(regex, "$1");
	return val == queryStr ? false : val;
}
// load XML document
function loadXMLDoc(filename)
{
  xhttps=new XMLHttpRequest();
  xhttps.open("GET",filename,false);
  xhttps.send();
  return xhttps.responseXML;
}

// get PNS text
function createPNS(type,date){
  var xmlDoc=loadXMLDoc("/source/lwx/pnsplot/"+type+"/"+date+"/WBCPNSLWX_"+date+".xml");
  var pns=xmlDoc.getElementsByTagName("text")[0].textContent;   

  var pnsNoMeatadata=pns.replace(/(METADATA)(.*)(,)/g,'TESTING');     
  

  $("#pns").html('<pre>'+pnsNoMeatadata+'</pre>');
}

// get event dates for type and return most recent date
function createDropDown(type){
  var xmlDoc=loadXMLDoc("/source/lwx/pnsplot/"+type+"/dates.xml");
  var dates=xmlDoc.getElementsByTagName("date");
  var x = document.getElementById("date");
  for(i=0;i<dates.length;i++){
    var d=dates[i].textContent;
    var option = document.createElement("option");
    option.text = d;
    option.value = d;
    x.add(option, x[i]);
  }
  return dates[0].textContent;
}

// create data options
function createOptions(type){
  var options = [];
  var labels = []; 
  switch(type){
    case "rain": options = [ 'rain', 'rain24' ];
                 labels = [ 'Storm Total Rainfall', '24 Hour Rainfall' ];
                 break;
    case "snow": options = [ 'csnow','snow', 'snow24', 'snowd', 'ice', 'ice24' ];
                 labels = [ 'Storm Total and 24 Hour Snowfall','Storm Total Snowfall', '24 Hour Snowfall', 'Snow Depth', 'Storm Total Ice', '24 Hour Ice' ];
                 break;
    case "wind": options = [ 'wind' ];
                 labels = [ 'Maximum Wind Gust' ];
                 break;
  }
  var x = document.getElementById("option");
  for(i=0;i<options.length;i++){
    var option = document.createElement("option");
    option.text = labels[i];
    option.value = options[i];
    x.add(option, x[i]);
  }
}

