	/********************************************************
	This Script will be included in all web pages 
	to show an Icon on Mouse over, 
	
	Clicking the Icon will show a Zoominto Viewer (a Flash made viewer) to Zoom the Image Content
	
	********************************************************/
	var tmppluginServername = "https://www.zoominto.com/zoomapi/";
//tmppluginServername= "https://localhost:9999/zoomapi/"
	var versionswf="1100";
	
	versionswf="";
	
	var tmppluginSwfname=""
	
	tmppluginSwfname=tmppluginServername  + "zoomplugin" + versionswf + ".swf";
	
	var tmppluginimageURL=tmppluginServername + "displayimage.php?image=";
	
    var protocol = "https://";
    
    var isInternetExplorer = navigator.appName.indexOf("Microsoft") != -1;

	plugin_run();
	 
    function BrowserZoomPlugin() {
        var ua, s, i;
        this.isIE = false;
        this.isNS = false;
        this.version = null; 
        ua = navigator.userAgent;
        s = "MSIE";
        if ((i = ua.indexOf(s)) >= 0) {
            this.isIE = true;
            this.version = parseFloat(ua.substr(i + s.length));
            return;
        }
        s = "Netscape6/";
        if ((i = ua.indexOf(s)) >= 0) {
            this.isNS = true;
            this.version = parseFloat(ua.substr(i + s.length));
            return;
        }
        s = "Gecko";
        if ((i = ua.indexOf(s)) >= 0) {
            this.isNS = true;
            this.version = 6.1;
            return;
        }
    }

    var browser = new BrowserZoomPlugin;
    var dragObj = new Object;
    dragObj.zIndex = 0;

    function zoominto_dragStart(event, id) {
        var el;
        var x, y;
        if (id) {
            dragObj.elNode = document.getElementById(id);
        } else {
            if (browser.isIE) {
                dragObj.elNode = window.event.srcElement;
            }
            if (browser.isNS) {
                dragObj.elNode = event.target;
            }
            if (dragObj.elNode.nodeType == 3) {
                dragObj.elNode = dragObj.elNode.parentNode;
            }
        }
        if (browser.isIE) {
            x = window.event.clientX + document.documentElement.scrollLeft + document.body.scrollLeft;
            y = window.event.clientY + document.documentElement.scrollTop + document.body.scrollTop;
        }
        if (browser.isNS) {
            x = event.clientX + window.scrollX;
            y = event.clientY + window.scrollY;
        }
        dragObj.cursorStartX = x;
        dragObj.cursorStartY = y;
        dragObj.elStartLeft = parseInt(dragObj.elNode.style.left, 10);
        dragObj.elStartTop = parseInt(dragObj.elNode.style.top, 10);
        if (isNaN(dragObj.elStartLeft)) {
            dragObj.elStartLeft = 0;
